Property.Value:
BEGIN
SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT COUNT(*) FROM [Account].[Account] WHERE Email = @Email END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
DECLARE @AccountId UNIQUEIDENTIFIER SELECT @AccountId = [Account].[Account].[AccountId] FROM [Account].[Account] -- [1] Administrator SELECT account.*,Role.RoleName FROM [Account].[Account] account INNER JOIN [Auth].[AccountRole] accountRole ON account.AccountId = accountRole.AccountId INNER JOIN [Auth].[Role] ON accountRole.RoleId = Role.RoleId WHERE account.Email = @Email
END

