CREATE PROCEDURE [Account].[GetByRoleName]
	@RoleName			NVARCHAR(50),
	@SearchKeyWord		NVARCHAR(150),
	@PageSize			INT = 1000,
	@PageNumber			INT = 1
AS

BEGIN
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	INSERT INTO [Account].[Account] (
		AccountId,
		Email,
		NormalizedEmail,
		UserName,
		NormalizedUserName,
		EmailConfirmed, 
		PasswordHash,
		FirstName,
		LastName,
		Status,
		CreatedOn,
		CreatedBy,
		IsDeleted,
		SecurityStamp,
		LockoutEndDateUtc,
		LockoutEnabled,
		AccessFailedCount
	) VALUES (
		@AccountId,
		@Email,
		@NormalizedEmail,
		@UserName,
		@NormalizedUserName,
		@EmailConfirmed, 
		@PasswordHash,
		@FirstName,
		@LastName,
		@Status,
		@CreatedOn,
		@CreatedBy,
		@IsDeleted,
		@SecurityStamp,
		@LockoutEndDateUtc,
		@LockoutEnabled,
		@AccessFailedCount
	)

END

