-- =============================================
-- Author:		Ahmed Dessouki
-- Create date: 1 Oct 2019
-- Description:	Country Update IsDeleted
-- =============================================
Create PROCEDURE [dbo].[CountryUpdateIsDelete] 
	@CountryId			UNIQUEIDENTIFIER,
	@LastModifiedOn		DATETIME,
	@LastModifiedBy		UNIQUEIDENTIFIER,
	@IsDeleted			BIT
AS

BEGIN SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET NOCOUNT ON;
SELECT destination.*,country.Name AS CountryName FROM [dbo].[Destination] destination INNER JOIN [dbo].[Country] country ON destination.CountryId = country.CountryId
WHERE destination.CountryId = @CountryId AND destination.IsDeleted = @IsDeleted END

BEGIN SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET NOCOUNT ON;
SELECT destination.*,country.Name AS CountryName FROM [dbo].[Destination] destination INNER JOIN [dbo].[Country] country ON destination.CountryId = country.CountryId
WHERE destination.DestinationId = @DestinationId END

BEGIN
			SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

			INSERT INTO [dbo].[Destination]
					   (
					   [DestinationId],
					   [CountryId],
					   [Name],
					   [IsApproved],
					   [CreatedOn],
					   [CreatedBy],
					   [IsDeleted]
					   )
				 VALUES
					   (
					   @DestinationId,
					   @CountryId,
					   @Name,
					   @IsApproved,
					   @CreatedOn,
					   @CreatedBy,
					   @IsDeleted 
					   )		
END

