-- =============================================
-- Author:		Shaker Mohamed
-- Create date: 29 Oct 2019
-- Description:	Destination UpdateIsDelete
-- =============================================
CREATE PROCEDURE [dbo].[DestinationUpdateIsDelete]
	@DestinationId				UNIQUEIDENTIFIER,
	@LastModifiedOn		DATETIME,
	@LastModifiedBy		UNIQUEIDENTIFIER,
	@IsDeleted			BIT
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
DELETE FROM [dbo].[FileUpload] WHERE [FileId] = @FileId END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET NOCOUNT ON;
SELECT * FROM [dbo].[FileUpload] WHERE FileId = @FileId END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT fileUpload.*,cat.Title as EntityName FROM [dbo].[FileUpload] fileUpload INNER JOIN [Website].[Category] cat ON fileUpload.EntityId = cat.CategoryId WHERE cat.WebSiteId = @WebSiteId AND fileUpload.IsDeleted = @IsDeleted END

BEGIN

	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	INSERT INTO [dbo].[FileUpload]
           (
			   [FileId],
			   [FileName],
			   [FileTitle],
			   [FileSize],
			   [EntityType],
			   [EntityId],
			   [CreatedOn],
			   [CreatedBy],
			   [IsDeleted]
		   )
     VALUES
           (
				@FileId,
				@FileName,
				@FileTitle,
				@FileSize,
				@EntityType,
				@EntityId,
				@CreatedOn,
				@CreatedBy,
				@IsDeleted
			)
END

