	@DestinationId		UNIQUEIDENTIFIER,
	@IsDeleted			BIT,
	@IsApproved			BIT = NULL,
	@LoggedInAccountId	UNIQUEIDENTIFIER = NULL
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT place.*,destination.Name AS DestinationName, country.Name AS CountryName FROM [dbo].Place place INNER JOIN [dbo].DestinationId destination ON place.DestinationId = destination.DestinationId INNER JOIN [dbo].[Country] country ON destination.CountryId = country.CountryId WHERE PlaceId = @PlaceId END

BEGIN

	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	INSERT INTO [dbo].[Place]
           (
			   [PlaceId],
			   [DestinationId],
			   [Name],
			   [IsApproved],
			   [CreatedOn],
			   [CreatedBy],
			   [IsDeleted]
		   )
     VALUES
           (
				@PlaceId,
				@DestinationId,
				@Name,
				@IsApproved,
				@CreatedOn,
				@CreatedBy,
				@IsDeleted
			)
END

