	@EntertainmentId	UNIQUEIDENTIFIER,
	@LastModifiedOn		DATETIME,
	@LastModifiedBy		UNIQUEIDENTIFIER,
	@IsDeleted			BIT
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Brand] WHERE IsDeleted = @IsDeleted END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Brand] WHERE BrandId = @BrandId END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Brand] brand INNER JOIN [Website].[WebSite] website ON brand.WebSiteId = website.WebSiteId WHERE website.WebSiteDomain= @WebSiteDomain AND brand.IsDeleted = @IsDeleted END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Brand] WHERE WebSiteId = @WebSiteId AND IsDeleted = @IsDeleted END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
INSERT INTO [Website].[Brand] ([BrandId] ,[BrandName] ,[ImageFile] ,[BrandOrder] ,[BrandLink] ,[WebsiteId] ,[Description] ,[CreatedOn] ,[CreatedBy] ,[IsDeleted]) VALUES ( @BrandId ,@BrandName ,@ImageFile ,@BrandOrder ,@BrandLink ,@WebsiteId ,@Description ,@CreatedOn ,@CreatedBy ,@IsDeleted ) END

