BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Category] category INNER JOIN [Website].[WebSite] wesite ON category.WebSiteId = wesite.WebSiteId WHERE wesite.WebSiteDomain= @WebSiteDomain AND category.IsDeleted = @IsDeleted END

BEGIN

	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

