			   [CreatedBy],
			   [IsDeleted]
		   )
     VALUES
           (
			   @CategoryId,
			   @WebSiteId,
			   @parentId,
			   @Title,
			   @ImageFile,
			   @Description,
			   @CategoryLevel,
			   @CategoryOrder,
			   @CreatedOn,
			   @CreatedBy,
			   @IsDeleted
		   )
