	@CreatedOn			 DATETIME,
	@CreatedBy			 UNIQUEIDENTIFIER,
	@IsDeleted			 BIT
AS

-- =============================================
-- Author:		Ahmed Dessouki
-- Create date: 26 Oct 2019
-- Description:	WebSite Event Update
-- =============================================
CREATE PROCEDURE [Website].[ProjectUpdate] 
	@ProjectId			 UNIQUEIDENTIFIER,
	@ProjectTitle		 NVARCHAR(255),
	@Images				 NVARCHAR(MAX),
	@CoverImage			 NVARCHAR(50),
	@ProjectLink		 NVARCHAR(150),
	@ProjectOrder		 INT,
	@Description		 NVARCHAR(MAX),
	@ProjectTag			 NVARCHAR(150),
	@LastModifiedOn		 DATETIME,
	@LastModifiedBy		 UNIQUEIDENTIFIER
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Service] WHERE ServiceId = @ServiceId END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Service] WHERE parentId = @parentId AND IsDeleted = @IsDeleted END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Service] Service INNER JOIN [Website].[WebSite] wesite ON Service.WebSiteId = wesite.WebSiteId WHERE wesite.WebSiteDomain= @WebSiteDomain AND Service.IsDeleted = @IsDeleted END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT service.*, parentService.Title as ParentName FROM [Website].[Service] service LEFT OUTER JOIN [Website].[Service] parentService on parentService.ServiceId = service.parentId WHERE service.WebSiteId = @WebSiteId AND service.IsDeleted = @IsDeleted END

BEGIN

	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	INSERT INTO [Website].[Service]
           (
		   [ServiceId],
           [WebSiteId],
           [parentId],
           [Title],
           [ImageFile],
		   [ServiceOrder],
           [Description],
		   [ServiceLevel],
		   [CreatedBy],
		   [CreatedOn],
		   [IsDeleted]
		   )
     VALUES
           (
		   @ServiceId,
           @WebSiteId,
           @parentId,
           @Title,
           @ImageFile,
		   @ServiceOrder,
           @Description,
		   @ServiceLevel,
		   @CreatedBy,
		   @CreatedOn,
		   @IsDeleted
		   )
END

