-- Author:		Shaker Mohamed
-- Create date: 23 Oct 2019
-- Description:	Website Service Insert
-- =============================================
CREATE PROCEDURE [Website].[ServiceInsert] 
	@ServiceId		UNIQUEIDENTIFIER,
	@WebSiteId		UNIQUEIDENTIFIER,
	@parentId		UNIQUEIDENTIFIER,
	@Title			NVARCHAR(150),
	@ImageFile		NVARCHAR(50),
	@ServiceOrder	INT,
	@ServiceLevel	INT,
	@Description	NVARCHAR(max),
	@CreatedBy		UNIQUEIDENTIFIER,
	@CreatedOn		DATETIME,
	@IsDeleted		BIT
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
UPDATE [Website].[Service] SET [parentId] = @parentId, [Title] = @Title, [ImageFile] = @ImageFile, [Description] = @Description, [ServiceLevel] = @ServiceLevel, [ServiceOrder] = @ServiceOrder, [LastModifiedOn] = @LastModifiedOn, [LastModifiedBy] = @LastModifiedBy WHERE [ServiceId] = @ServiceId END

