BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET NOCOUNT ON; SELECT * FROM [Website].[Topic] WHERE WebSiteId = @WebSiteId AND IsDeleted = @IsDeleted END


BEGIN

	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	INSERT INTO [Website].[Topic]
           (
				[TopicId],		
				[WebSiteId],     
				[TopicTitle],	
				[TopicTag],	
