-- =============================================
-- Author:		Shaker Mohamed
-- Create date: 26 Sept 2019
-- Description:	Website Get All
-- =============================================
CREATE PROCEDURE [Website].[WebsiteGetAll] 
	@IsDeleted			BIT,
	@SearchKeyWord		NVARCHAR(150),
	@PageSize			INT = 1000,
	@PageNumber			INT = 1
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET NOCOUNT ON;
SELECT wHotel.*, website.WebSiteDomain, hotel.Name AS HotelName, desitantion.Name AS DestinationName, desitantion.DestinationId, country.Name AS CountryName, country.CountryId FROM [Website].[WebSiteHotel] wHotel INNER JOIN [Website].[WebSite] website ON wHotel.WebSiteId = website.WebSiteId INNER JOIN [dbo].[Hotel] hotel ON wHotel.HotelId = hotel.HotelId INNER JOIN [dbo].[Destination] desitantion ON hotel.DestinationId = desitantion.DestinationId INNER JOIN [dbo].[Country] country ON desitantion.CountryId = country.CountryId WHERE wHotel.WebSiteHotelId = @WebSiteHotelId END

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET NOCOUNT ON;
SELECT wHotel.*, website.WebSiteDomain, hotel.Name AS HotelName, desitantion.Name AS DestinationName, desitantion.DestinationId, country.Name AS CountryName, country.CountryId FROM [Website].[WebSiteHotel] wHotel INNER JOIN [Website].[WebSite] website ON wHotel.WebSiteId = website.WebSiteId INNER JOIN [dbo].[Hotel] hotel ON wHotel.HotelId = hotel.HotelId INNER JOIN [dbo].[Destination] desitantion ON hotel.DestinationId = desitantion.DestinationId INNER JOIN [dbo].[Country] country ON desitantion.CountryId = country.CountryId WHERE (website.WebSiteDomain = @WebSiteDomain OR @WebSiteDomain IS NULL) AND (website.WebSiteId = @WebSiteId OR @WebSiteId IS NULL) AND (wHotel.IsDeleted = @WebSiteHotelIsDeleted OR @WebSiteHotelIsDeleted IS NULL) AND (hotel.IsDeleted = @HotelIsDeleted OR @HotelIsDeleted IS NULL) ORDER BY desitantion.Name END

