-- =============================================
-- Author:		Shaker Mohamed
-- Create date: 25 Sept 2019
-- Description:	Website Insert
-- =============================================
CREATE PROCEDURE [Website].[WebsiteInsert] 
	@WebSiteId			 UNIQUEIDENTIFIER,
	@WebSiteDomain		 NVARCHAR(150),
	@WebSiteLogo		 NVARCHAR(100),
	@WebSiteSocialMedia	 NVARCHAR(MAX),
	@AccountId			 UNIQUEIDENTIFIER,
	@CreatedOn			 DATETIME,
	@CreatedBy			 UNIQUEIDENTIFIER,
	@IsDeleted			 BIT,
	@Status				 INT
AS

BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET NOCOUNT ON;
SELECT wPlace.*, website.WebSiteDomain, place.Name AS PlaceName, desitantion.Name AS DestinationName, desitantion.DestinationId, country.Name AS CountryName, country.CountryId FROM [Website].[WebSitePlace] wPlace INNER JOIN [Website].[WebSite] website ON wPlace.WebSiteId = website.WebSiteId INNER JOIN [dbo].[Place] place ON wPlace.PlaceId = place.PlaceId INNER JOIN [dbo].[Destination] desitantion ON place.DestinationId = desitantion.DestinationId INNER JOIN [dbo].[Country] country ON desitantion.CountryId = country.CountryId WHERE (website.WebSiteDomain = @WebSiteDomain OR @WebSiteDomain IS NULL) AND (website.WebSiteId = @WebSiteId OR @WebSiteId IS NULL) AND (wPlace.IsDeleted = @WebSitePlaceIsDeleted OR @WebSitePlaceIsDeleted IS NULL) AND (place.IsDeleted = @PlaceIsDeleted OR @PlaceIsDeleted IS NULL) ORDER BY desitantion.Name END

